async () => {
  const queueItems = (await db.get("Queue")).filter(
    (x) => x.type === "puppeteer"
  );

  if (queueItems.length === 0) return;

  const lateQueueItemsToNotify = queueItems.filter(
    (x) =>
      x.notifyLateAfterSeconds &&
      Date.now() - x.createdAt > x.notifyLateAfterSeconds * 1000 &&
      !x.hasNotifiedLate
  );

  // 1. check how many chrome tabs are available (according to OS settings / memory limit)
  const idlePages = (await db.get("BrowserPage")).filter((p) => p.isIdle);

  const runnableNowAmount = 1; //idlePages.length
  const queueItemsToExecute = queueItems.slice(0, runnableNowAmount);

  if (runnableNowAmount < lateQueueItemsToNotify.length) {
    //email the ones that won't be executed
    console.log(
      {
        runnableNowAmount,
        lateQueueItemsToNotify: lateQueueItemsToNotify.length,
      },
      "SHOULD EMAIL SOME THAT CAN'T BE EXECUTED AND PUT THEM ON NOTIFIED WITH ETA"
    );
  }

  if (idlePages.length === 0) return;
  // 2. check how many queue items can be executed now, and sort by importancy
  // NB: limit to 1 for now to test if that works

  // 3. take the ones that are most important and execute those
  await Promise.all(
    queueItemsToExecute.map(async (queue) => {
      const fn = sdk[queue.functionName as keyof typeof sdk] as
        | ((...parameters: any[]) => any)
        | undefined;

      if (!fn) return;

      await fn(...queue.parameters);
    })
  );

  const removeResult = await db.remove("Queue", (item) =>
    queueItemsToExecute.map((x) => x.id).includes(item.id)
  );
  console.log(`executed`, { removeResult });
}
