 async (
  context: GetStaticPropsContext
) => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) {
    console.log("NO PROJECTROOT");

    const props: ReaderProps = {
      notFound: true,
      notFoundReason: "No projectroot",
    };

    return { props };
  }

  // the queryPath from
  const queryPath = context.params?.queryPath;
  const realQueryPath = queryPath ? makeArray(queryPath).join("/") : "";

  const basePath = process.env.NEXT_PUBLIC_BASEPATH;
  // can probably also fetch the domain using the api, right? I might be able to determine what the basepath is based on an environment variable.
  // console.log({ basePath });

  const realBasePath = basePath
    ? path.join(projectRoot, basePath)
    : projectRoot;

  const absoluteQueryPath = path.join(realBasePath, realQueryPath);

  const projectRelativeFilePath = makeRelative(absoluteQueryPath, projectRoot);

  const staticPropResult = omitUndefinedValues(
    await getReaderPageProps(projectRelativeFilePath, basePath)
  );

  return staticPropResult;

