export const ReaderPageNext = (props: ReaderProps) => {
  // props provide the static data for the website

  const basePath = process.env.NEXT_PUBLIC_BASEPATH;
  const readerPageQuery = queries.useGetReaderPageProps(
    props.projectRelativeFilePath,
    basePath
  );
  const queryProps = readerPageQuery.data?.result?.props;

  // either use the props returned from the query or from the static site
  const realProps = queryProps?.projectRelativeFilePath ? queryProps : props;

  return <ReaderPage {...realProps} />;
};
