value:
(props: {
  notFound?: boolean;
  projectRelativeFilePath?: string;
  folderPath?: string;
  filename?: string;
  isFolder?: boolean;
  navigation?: FolderContent[];
  contextualPromptsObject?: Omit<
    ContextualPromptsObject,
    "databaseContextualPromptSlugs"
  > | null;
  fileContextualPromptResults: ContextualPromptResult[] | undefined;
  thePrompts: ContextualPrompt[];
  selectionContextualPromptResults: ContextualPromptResult[] | undefined;
}) => {
  const {
    isFolder,
    navigation,
    folderPath,
    filename,
    contextualPromptsObject,
    fileContextualPromptResults,
    selectionContextualPromptResults,
    thePrompts,
    notFound,
  } = props;

  const admin = useAdmin();
  const basePath = process.env.NEXT_PUBLIC_BASEPATH;

  const updateFrontmatter = async (
    projectRelativePath: string | undefined,
    newFrontmatter: { [key: string]: string }
  ) => {
    if (!projectRelativePath) return;

    if (!basePath) return;

    const apiResult = await api.updateFrontmatter({
      projectRelativePath,
      frontmatter: newFrontmatter,
    });

    showStandardResponse(apiResult);

    if (apiResult.result?.isSuccessful) {
      admin.refetch?.();
    }
  };

  const alert = useAlert();
  const { openContextMenuProps, renderContextMenu } = useContextMenu({
    items: [
      {
        getTitle: () => "üóë Delete",
        onClick: (projectRelativePath) => {
          if (!projectRelativePath) return;

          if (!basePath) return;

          const basePathRelative = makeRelative(projectRelativePath, basePath);
          alert?.(
            "Are you sure?",
            `Do you want to delete ${basePathRelative}?`,
            [
              { style: "cancel", text: "Cancel" },
              {
                text: "Delete",
                style: "destructive",
                onPress: async () => {
                  // delete also works, but trash seems better for now.
                  const apiResult = await api.deleteFileOrFolder(
                    projectRelativePath
                  );

                  showStandardResponse(apiResult);

                  if (apiResult?.result?.isSuccessful) {
                    admin.refetch?.();
                  }
                },
              },
            ]
          );
        },
      },
      {
        getTitle: () => "üì£ Rename",
        onClick: async (projectRelativePath) => {
          if (!projectRelativePath) return;
          let newFilename = prompt(
            "What should the new name be?",
            getFileOrFolderName(projectRelativePath)
          );
          if (!newFilename) return;
          const apiResult = await api.renameFileOrFolder(
            projectRelativePath,
            newFilename
          );

          showStandardResponse(apiResult);
          if (apiResult?.result?.isSuccessful) {
            admin.refetch?.();
          }
        },
      },
      {
        getTitle: () => "‚Ü©Ô∏è Move",
        onClick: async (projectRelativePath) => {
          if (!projectRelativePath) return;

          if (!basePath) return;

          const basePathRelative = makeRelative(projectRelativePath, basePath);
          let newLocation = prompt(
            "Where should this file/folder be moved to?",
            getFolderJs(basePathRelative)
          );
          if (!newLocation) return;

          const apiResult = await api.movePath(
            projectRelativePath,
            basePath + "/" + newLocation
          );

          showStandardResponse(apiResult);
          if (apiResult?.result?.isSuccessful) {
            admin.refetch?.();
          }
        },
      },

      {
        getTitle: () => "üëØ‚Äç‚ôÄÔ∏è Make a copy",
        onClick: async (projectRelativePath) => {
          if (!projectRelativePath) return;

          const apiResult = await api.copyPath(projectRelativePath);

          showStandardResponse(apiResult);

          if (apiResult?.result?.isSuccessful) {
            admin.refetch?.();
          }
        },
      },

      {
        getTitle: () => "üîé Open in Explorer",
        onClick: async (projectRelativePath) => {
          if (!projectRelativePath) return;

          if (!basePath) return;

          const apiResult = await api.fileExplorerOpen(projectRelativePath);

          showStandardResponse(apiResult);
        },
      },

      {
        getTitle: () => "üíª Open in VSCode",
        onClick: async (projectRelativePath) => {
          if (!projectRelativePath) return;

          if (!basePath) return;

          const apiResult = await api.vscodeOpen({
            files: [{ projectRelativePath }],
          });

          showStandardResponse(apiResult);
        },
      },

      {
        getIsEnabled: (projectRelativePath) =>
          navigation?.find((x) => x.projectRelativePath === projectRelativePath)
            ?.authorizedGroup !== "",
        getTitle: () => "üïä Make free",
        onClick: (path) => updateFrontmatter(path, { authorizedGroup: "" }),
      },
      {
        getIsEnabled: (projectRelativePath) =>
          navigation?.find((x) => x.projectRelativePath === projectRelativePath)
            ?.authorizedGroup !== "premium",

        getTitle: () => "üèõ Make indie-only",
        onClick: (path) =>
          updateFrontmatter(path, { authorizedGroup: "premium" }),
      },

      {
        getIsEnabled: (projectRelativePath) =>
          navigation?.find((x) => x.projectRelativePath === projectRelativePath)
            ?.authorizedGroup !== "enterprise",
        getTitle: () => "üöÄ Make startup-only",
        onClick: (path) =>
          updateFrontmatter(path, { authorizedGroup: "enterprise" }),
      },

      {
        getIsEnabled: (projectRelativePath) =>
          !navigation?.find(
            (x) => x.projectRelativePath === projectRelativePath
          )?.isPrivate,
        getTitle: () => "üîë Set private",
        onClick: (path) => updateFrontmatter(path, { isPrivate: "true" }),
      },
      {
        getIsEnabled: (projectRelativePath) =>
          navigation?.find((x) => x.projectRelativePath === projectRelativePath)
            ?.isPrivate === true,
        getTitle: () => "üîë Set public",
        onClick: (path) => updateFrontmatter(path, { isPrivate: "false" }),
      },
      {
        getIsEnabled: (projectRelativePath) =>
          navigation?.find((x) => x.projectRelativePath === projectRelativePath)
            ?.isSecret !== true,
        getTitle: () => "üîí Set secret",
        onClick: (path) => updateFrontmatter(path, { isSecret: "true" }),
      },
      {
        getIsEnabled: (projectRelativePath) =>
          navigation?.find((x) => x.projectRelativePath === projectRelativePath)
            ?.isSecret === true,
        getTitle: () => "üîí Set open",
        onClick: (path) => updateFrontmatter(path, { isSecret: "false" }),
      },
      {
        getIsEnabled: (projectRelativePath) =>
          navigation?.find((x) => x.projectRelativePath === projectRelativePath)
            ?.isDraft !== true,
        getTitle: () => "üß™ Set draft",
        onClick: (path) => updateFrontmatter(path, { isDraft: "true" }),
      },
      {
        getIsEnabled: (projectRelativePath) =>
          navigation?.find((x) => x.projectRelativePath === projectRelativePath)
            ?.isDraft === true,
        getTitle: () => "üß™ Set live",
        onClick: (path) => updateFrontmatter(path, { isDraft: "false" }),
      },
    ],
  });

  const router = useRouter();
  const queryPath = router.asPath.slice(1);

  const basePathRelativeFolderPath = notFound
    ? "/"
    : isFolder
    ? queryPath
    : (getFolderJs(queryPath) as string | undefined);

  const folders = basePathRelativeFolderPath
    ?.split("/")
    .filter((x) => !!x && x !== "");
  const canGoBack = notFound
    ? false
    : queryPath.includes("/") || (isFolder && queryPath !== "");

  return (
    <Div className="lg:w-48 lg:h-full lg:overflow-y-scroll">
      {renderContextMenu()}
      <div className="flex sm:flex-row lg:flex-col flex-wrap lg:flex-nowrap">
        {/* root */}
        {canGoBack ? <NavButton href={"/README.md"} title="üè†" /> : null}
        {folders?.map((folder, index, array) => {
          const isActive =
            index === array.length - 1 &&
            (!filename || filename?.toLowerCase() === "readme.md");
          const pathUntilHere = array.slice(0, index + 1).join("/");
          return (
            <NavButton
              isActive={isActive}
              href={`/${pathUntilHere}`}
              title={`‚¨ÜÔ∏è ${folder}`}
            />
          );
        })}
        {navigation?.map((navItem) => {
          // if it's a file, we need to remove the name
          const href = `${notFound ? "" : folderPath}/${navItem.name}${
            navItem.type === "folder" && navItem.firstFile
              ? `/${navItem.firstFile}`
              : ""
          }`;

          const extraIcons = [
            navItem.isDraft ? "üß™" : undefined,
            navItem.isPrivate ? "üîë" : undefined,
            navItem.isSecret ? "üîí" : undefined,
            navItem.authorizedGroup === "premium"
              ? "üèõ"
              : navItem.authorizedGroup === "enterprise"
              ? "üöÄ"
              : undefined,
          ]
            .filter(notEmpty)
            .join("");

          const extension = navItem.name.split(".").pop()!;
          const extensionIcons = {
            ts: "‚åò",
            tsx: "‚åò",
            json: "üíø",
            md: "üìÑ",
          };
          const icon =
            navItem.type === "file"
              ? Object.keys(extensionIcons).includes(extension)
                ? extensionIcons[extension as keyof typeof extensionIcons]
                : "üìÑ"
              : "üìÇ";
          const basePath = process.env.NEXT_PUBLIC_BASEPATH;
          const projectRelativePath = `${basePath || ""}${
            href.startsWith("/") ? href : `/${href}`
          }`;

          return (
            <NavButton
              openContextMenuProps={
                admin.isAdminActive ? openContextMenuProps : undefined
              }
              isActive={filename === navItem.name}
              href={href}
              id={projectRelativePath}
              title={`${icon} ${navItem.name} ${extraIcons}`}
            />
          );
        })}

        {admin.isAdminActive ? (
          <>
            <NavButton
              onClick={async () => {
                if (!basePath) return;
                let filename = prompt(
                  "What should be the name (including extension)?",
                  "untitled.md"
                );
                if (!filename || filename === "") return;

                const projectRelativePath = `${basePath}/${basePathRelativeFolderPath}/${filename}`;

                const apiResult = await api.newFile(projectRelativePath);

                showStandardResponse(apiResult);

                if (apiResult?.result?.isSuccessful) {
                  admin.refetch?.();
                }
              }}
              title="+ New file"
            />
            <NavButton
              onClick={async () => {
                if (!basePath) return;
                let folderName = prompt("What should be the name?", "");
                if (!folderName || folderName === "") return;

                const projectRelativeBasePath = `${basePath}/${basePathRelativeFolderPath}`;

                const apiResult = await api.newFolder(
                  projectRelativeBasePath,
                  folderName
                );

                showStandardResponse(apiResult);

                if (apiResult?.result?.isSuccessful) {
                  admin.refetch?.();
                }
              }}
              title="+ New folder"
            />
          </>
        ) : null}
      </div>

      {admin.isAdminActive ? (
        <Div className="mx-2">
          <P>
            selectionResults: {selectionContextualPromptResults?.length || 0}
          </P>
          <P>fileResults: {fileContextualPromptResults?.length || 0}</P>
          <P>
            {isFolder ? "folderPrompts" : "pagePrompts"}: {thePrompts.length},
            selectionPrompts:{" "}
            {contextualPromptsObject?.selectionContextualPrompts.length || 0}
          </P>
          <div
            className="cursor-pointer"
            onClick={async () => {
              const apiResult = await api.removeAllFake(
                process.env.NEXT_PUBLIC_BASEPATH
              );
              showStandardResponse(apiResult);
            }}
          >
            Delete all fake results
          </div>
        </Div>
      ) : null}
    </Div>
  );
}
