value:
(props: {
  item: ContextualPrompt;
  markdown: string | null;
  projectRelativeFilePath: string;
}) => {
  const { item, markdown, projectRelativeFilePath } = props;
  const admin = useAdmin(projectRelativeFilePath);
  const router = useRouter();
  const showPromptAlert = usePromptResultAlert();

  const title = `${item.isFavorite ? "‚≠êÔ∏è " : ""}${item.title || item.name}`;

  return (
    <Tooltip
      tooltip={
        admin.isAdminActive ? (
          <Div>
            {item.title}
            <ClickableIcon
              emoji="üóë Delete"
              onClick={async () => {
                // delete prompt
                const apiResult = await api.deleteDbModel(
                  "ContextualPrompt",
                  item.id
                );

                showStandardResponse(apiResult);

                if (apiResult?.result?.isSuccesful) {
                  admin.refetch?.();
                }
              }}
            />
            <ClickableIcon
              emoji="‚úèÔ∏è Edit"
              onClick={() => {
                // go to upsert ContextualPrompt db page.
                router.push(`/edit/ContextualPrompt?id=${item.id}`);
              }}
            />
          </Div>
        ) : null
      }
      placement="top"
    >
      <NavButton
        onClick={async () => {
          if (!markdown) return;
          await processPrompt({
            contextualPromptSlug: item.slug,
            showPromptAlert,
            contextualContent: {
              contextContent: markdown,
              contextSelection: null,
              context_projectRelativeFilePath: projectRelativeFilePath,
            },
          });

          router.replace({
            query: {
              queryPath: router.query.queryPath,
              variant: item.slug,
            },
          });
        }}
        title={title}
      />
    </Tooltip>
  );
}
